package oprators;

public class devcriment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		  int a=3;
		  --a;
		  System.out.println(a);//2
		  
		  
		  System.out.println("perfix decrement opretor");
		  
		  //perfix decrement opretor
		  
		  int b=10;
		  System.out.println(b);  //10
		  System.out.println(--b);//9
		  System.out.println(b);  //9
		  System.out.println("------------------------------------------------------------");
		System.out.println("postfix decrement opretor");
		  
		  
		  //postfix decrement opretor
		  
		  int c=9;
		  
		  System.out.println(c); //9
		 System.out.println(c--);//9
		 System.out.println(c);  //8
		 
		 System.out.println("------------------------------------------------------------------");
		 // logical 
		 
		 int q=2;
		 int w=3;
		 int e= --q;
		 int r= w--;
		 System.out.println("-------------------------------------------------------------------");
		 
		 System.out.println(q);//1
		 System.out.println(w);//2
		 System.out.println(e);//1
		 System.out.println(r);//3
		 
		 //character literal 
		 
		 char x = 's';// the value of s is 115
		 System.out.println(--x); // r got printed
		 System.out.println("-------------------------------------------------------------------------");
		 
		 double y=8.34;
		 System.out.println(--y);
		// System.out.println(--5); decriment opretor can't work with interger litrel
		  
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		 
		
	}

}
