package oprators;

public class incriment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
		int a=9;
		++a; //prefix increment operetor
		System.out.println(a);//10
		
		int b=4;
		System.out.println(b);
		System.out.println(++b);
		System.out.println(b);//prefix increment operetor
		
		
		int c=5;
		System.out.println(c);

		System.out.println(c++);
		System.out.println(c);// postfix increment operetor
		
		
		
		//this is a logical program
		int t=2;
		int u=3;
		 
		int y=++u;
		int x=t++;
		System.out.println(t);//3
		System.out.println(u);//4
		System.out.println(y);//4
		System.out.println(x);//2
		
				
			//incriment opretor in char
		
		char w = 's'; // the interger value of 's' is 115 ASSCI TABLE
		
		System.out.println(++w);// incriment 1 value in t got printed becouse of the 
		//the value of t is 116
		
		double e = 3.53;// 
		System.out.println(++e);//4.53
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
