package oprators;

public class logical {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  //logical or oprator
		
		
        boolean a=true;
		boolean b=false;
		boolean c=true;
		boolean d=false;
		
		System.out.println(a||b); //true
		System.out.println(b||c); //true
		System.out.println(b||d); //false
		System.out.println(a||c); //true
		
		
	System.out.println("=================================================================================");
		
	// logical not operator 
		
		boolean e=true;
		boolean w=false;
		System.out.println(!e); //false
		System.out.println(!w );//true
		
		
		//logical and && operator
		
		boolean q=true;
		boolean o=false;
		boolean u=false;
		boolean y=true;
		boolean i=false;
		boolean n=false;
		boolean m=true;
		boolean h=true;
		
		System.out.println(q&&o);//false
		System.out.println(u&&y);//false
		System.out.println(i&&n);//false
		System.out.println(m&&h);//true
		
		System.out.println("--------------------------------------------------------------------------------------");
		
		
		
		
		
		
		
		
				
		
	}

}
